export const tryOptionalDeps = async () => {
  const results = { exports: {}, errors: {} };
  try {
    results.exports.alpha = await import('alpha');
  } catch (err) {
    results.errors.alpha = err;
  }
  try {
    results.exports.beta = await import('beta');
  } catch (err) {
    results.errors.beta = err;
  }
  try {
    results.exports.one = await import('missing-one');
  } catch (err) {
    results.errors.one = err;
  }
  try {
    results.exports.two = await import('missing-two');
  } catch (err) {
    results.errors.two = err;
  }
  return results
}
