const { freeze } = Object;
export const attenuate = (params, originalObject, optionallyGlobalThis) => {
  if(optionallyGlobalThis) {
    optionallyGlobalThis.attenuatorFlag = 1;
  }
  const ns = params.reduce((acc, k) => {
    acc[k] = originalObject[k];
    return acc;
  }, {});
  return freeze(ns);
};
